import React from 'react';
import '../styles/ReuseInfo.css';

function ReuseInfo() {
  return (
    <div className="reuse-info">
      <p className="reuse-info__intro">
        Con su ayuda hemos reutilizado miles de frascos de Milahuén para contribuir al medio ambiente. ¡Gracias por ser parte de este cambio!
      </p>
      <h2 className="reuse-info__counter">
        <span className="reuse-info__count">10,000</span> frascos reutilizados
      </h2>
      <div className="reuse-info__impact">
        <div className="reuse-info__impact-item">
          <img src="/path/to/tree-icon.png" alt="Icono de árbol" className="reuse-info__icon" />
          <p>Tala de árboles evitada</p>
        </div>
        <div className="reuse-info__impact-item">
          <img src="/path/to/water-icon.png" alt="Icono de agua" className="reuse-info__icon" />
          <p>Litros de agua ahorrados</p>
        </div>
      </div>
    </div>
  );
}

export default ReuseInfo;
