import React from 'react';
import BenefitCard from './BenefitCard';
import '../styles/BenefitsSection.css';

function BenefitsSection() {
  const benefits = [
    {
      id: 1,
      image: '/images/tree.jpg',
      title: 'Tala de árboles evitada',
      description: 'Al reutilizar frascos, reducimos la necesidad de tala de árboles.',
    },
    {
      id: 2,
      image: '/images/water.png',
      title: 'Ahorro de agua',
      description: 'La economía circular permite conservar miles de litros de agua.',
    },
  ];

  return (
    <div className="benefits-section">
      {/* Muestra cada beneficio usando BenefitCard */}
      {benefits.map((benefit) => (
        <BenefitCard
          key={benefit.id}
          image={benefit.image}
          title={benefit.title}
          description={benefit.description}
        />
      ))}

      {/* Sección adicional con descripción y recompensas */}
      <div className="benefits-section__additional">
        <div className="benefits-section__info">
          <p className="benefits-section__description">
            Reutilizar es una de las prácticas más efectivas en la economía circular.
            Al hacerlo, reducimos el impacto ambiental y promovemos un uso más consciente de los recursos.
          </p>
        </div>
        <div className="benefits-section__reward">
          <p>Reutiliza tus frascos y Milahuén te premia con descuentos especiales.</p>
          <img src="/images/reward.png" alt="Premios por reutilización" className="benefits-section__image" />
        </div>
      </div>
    </div>
  );
}

export default BenefitsSection;
