import React from 'react';
import Slider from 'react-slick';
import ProductCard from '../components/ProductCard';
import JoinSection from '../components/JoinSection';
import '../styles/ProductView.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

function ProductView() {
  // Ejemplo de datos de productos
  const products = [
    { id: 1, image: '/images/almendras.png', name: 'Almendras', price: 5000 },
    { id: 2, image: '/images/mani.jpg', name: 'Nueces', price: 4000 },
    { id: 3, image: '/images/nuez.jpeg', name: 'Maní', price: 3000 },
  ];

  // Configuración del carrusel
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="product-view">
      {/* Carrusel de productos destacados */}
      <div className="product-view__carousel">
        <Slider {...carouselSettings}>
          <div>
            <img src="/public/images/almendras.png" alt="Producto destacado 1" />
          </div>
          <div>
            <img src="/public/images/mani.jpg" alt="Producto destacado 2" />
          </div>
          <div>
            <img src="/public/images/nuez.jpeg" alt="Producto destacado 3" />
          </div>
        </Slider>
      </div>

      {/* Título de la sección de productos */}
      <h2 className="product-view__title">Nuestros Productos</h2>

      {/* Grid de productos */}
      <div className="product-view__grid">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            image={product.image}
            name={product.name}
            price={product.price}
          />
        ))}
      </div>

      {/* Sección de invitación */}
      <JoinSection />
    </div>
  );
}

export default ProductView;
