import React, { useState } from 'react';
import ReuseInfo from '../components/ReuseInfo';
import BenefitsSection from '../components/BenefitsSection';
import '../styles/ReuseView.css';

function ReuseView() {
  const [jarsReused, setJarsReused] = useState(100); // Número de frascos reutilizados

  const treesSaved = (jarsReused * 0.1).toFixed(1); // 0.1 árboles por frasco
  const waterSaved = (jarsReused * 0.9).toFixed(1); // 0.9 litros de agua por frasco

  return (
    <div className="reuse-view">
      <div className="reuse-view__header">
        <p>Con su ayuda hemos reutilizado miles de frascos de Milahuén para contribuir al medio ambiente. ¡Gracias por ser parte de este cambio!</p>
        <h2 className="reuse-view__title">
          <strong>{jarsReused.toLocaleString()}</strong> frascos reutilizados
        </h2>
      </div>

      <div className="reuse-view__stats">
        <p>Gracias a estos frascos reutilizados, se evitó la tala de aproximadamente <strong>{treesSaved}</strong> árboles y se ahorraron <strong>{waterSaved}</strong> litros de agua.</p>
      </div>

      <ReuseInfo />
      <BenefitsSection />
    </div>
  );
}

export default ReuseView;
